import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
// import player from 'react-player';
import { Player } from 'video-react';
import { loadModels, getFullFaceDescription, createMatcher } from '../api/face';
import DrawBox from '../components/drawBox';
import { JSON_PROFILE } from '../common/profile';

const WIDTH = 420;
const HEIGHT = 420;
const inputSize = 160;

class CameraFaceDetect extends Component {
  constructor(props) {
    super(props);
    this.player = React.createRef();
    this.state = {
      fullDesc: null,
      faceMatcher: null,
      facingMode: null
    };
  }

  componentWillMount() {
    loadModels();
    // this.setInputDevice();
    this.matcher();
  }

  // setInputDevice = () => {
  //   navigator.mediaDevices.enumerateDevices().then(async devices => {
  //     let inputDevice = await devices.filter(
  //       device => device.kind === 'videoinput'
  //     );
  //     if (inputDevice.length < 2) {
  //       await this.setState({
  //         facingMode: 'user'
  //       });
  //     } else {
  //       await this.setState({
  //         facingMode: { exact: 'environment' }
  //       });
  //     }
  //     this.startCapture();
  //   });
  // };

  matcher = async () => {
    const faceMatcher = await createMatcher(JSON_PROFILE);
    this.setState({ faceMatcher });
  };

  // startCapture = () => {
  //   this.interval = setInterval(() => {
  //     this.capture();
  //   }, 1500);
  // };

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  capture = async () => {
    if (!!this.player.readyState) {
      await getFullFaceDescription(
        this.player.current.getScreenshot(),
        inputSize
      ).then(fullDesc => this.setState({ fullDesc }));
    }
  };

  render() {
    const { fullDesc, faceMatcher, facingMode } = this.state;
    let videoConstraints = null;
    let camera = '';
    // if (!!facingMode) {
    //   videoConstraints = {
    //     width: WIDTH,
    //     height: HEIGHT,
    //     facingMode: facingMode
    //   };
    //   if (facingMode === 'user') {
    //     camera = 'Front';
    //   } else {
    //     camera = 'Back';
    //   }
    // }

    return (
      <div
        className="Camera"
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}
      >
        <p>Camera: {camera}</p>
        <div
          style={{
            width: WIDTH,
            height: HEIGHT
          }}
        >
          <div style={{ position: 'relative', width: WIDTH }}>
            {!!videoConstraints ? (
              <div style={{ position: 'absolute' }}>
                <Player 
                  autoPlay
                  width={WIDTH}
                  height={HEIGHT}
                  src="https://media.w3.org/2010/05/sintel/trailer_hd.mp4"
                  screenshotFormat="image/jpeg"
                  videoConstraints={videoConstraints}
                />
              </div>
            ) : null}
            {!!fullDesc ? (
              <DrawBox
                fullDesc={fullDesc}
                faceMatcher={faceMatcher}
                imageWidth={WIDTH}
                boxColor={'blue'}
              />
            ) : null}
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(CameraFaceDetect);
